CREATE TABLE IF NOT EXISTS usuario (id INTEGER PRIMARY KEY AUTOINCREMENT, nome VARCHAR(100) NOT NULL, senha VARCHAR(100) NOT NULL);
CREATE TABLE IF NOT EXISTS musica (id INTEGER PRIMARY KEY AUTOINCREMENT, nome VARCHAR(100) NOT NULL, autor VARCHAR(100), letra text, traducao text, album VARCHAR(100));
CREATE TABLE IF NOT EXISTS favorita (id_usuario INTEGER NOT NULL, id_musica INTEGER NOT NULL, FOREIGN KEY (id_usuario) REFERENCES usuario(id), FOREIGN KEY (id_musica) REFERENCES musica(id))